import React, { useState } from 'react';
import './index.css';
import twitImg from "./images/twitter.png";
import fbImg from "./images/fb1.png";
import instaImg from "./images/instagram.png";

function Contact() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/contact', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                alert('Message sent successfully!');
                setFormData({ name: '', email: '', message: '' });
            } else {
                alert('Failed to send message.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while sending the message.');
        }
    };

    return (
        <div className="container">
            <div className="form-section">
                <form className="form" onSubmit={handleSubmit}>
                    <h1>Contact Us</h1>
                    <div className="mb-3">
                        <div className="form-floating">
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                            />
                            <label>Enter your name</label>
                        </div>
                    </div>
                    <div className="mb-3">
                        <div className="form-floating">
                            <input
                                type="email"
                                className="form-control"
                                placeholder="Email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                            />
                            <label>Enter a valid Email Address</label>
                        </div>
                    </div>
                    <div className="mb-3">
                        <div className="form-floating">
                            <textarea
                                className="form-control"
                                placeholder="Leave a comment here"
                                name="message"
                                value={formData.message}
                                onChange={handleChange}
                                required
                            ></textarea>
                            <label>Comments</label>
                        </div>
                    </div>
                    <div>
                        <button className="submit-button" type="submit">Submit</button>
                    </div>
                </form>
            </div>
            <div className="right-section">
                <div className="info-box">
                    <div className="info-item">
                        <strong>CALL US</strong><br />
                        1234567890<br />
                        0437234567
                    </div>
                    <div className="info-item">
                        <strong>LOCATION</strong><br />
                        10-146, Indira Nagar, Bapulapadu<br />
                        Andhra Pradesh, India<br /> Pin Code: 521105
                    </div>
                    <div className="info-item">
                        <strong>BUSINESS HOURS</strong><br />
                        Mon – Fri  10 am – 8 pm<br />
                        Sat, Sun Closed
                    </div>
                </div>
            </div>
            <div className="pink-section">
                <div className="get-in-touch">GET IN TOUCH</div>
                <p className="sub-text">
                    Hey! We are looking forward to start a project with you!
                </p>
                <div className="social-icons">
                    <a href="https://www.facebook.com/satyamanikanta.bogolu" className="icon"><img src={fbImg} alt="Facebook" /></a>
                    <a href="https://www.instagram.com/_satya_15/" className="icon"><img src={instaImg} alt="Instagram" /></a>
                    <a href="#" className="icon"><img src={twitImg} alt="Twitter" /></a>
                </div>
            </div>
        </div>
    );
}

export default Contact;
