import React from 'react';
import styled from 'styled-components';

const ContactSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 50px;
  background-color: #111;
  color: #fff;
`;

const FormWrapper = styled.div`
  background: #222;
  padding: 40px;
  border-radius: 8px;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 600px;
  margin-bottom: 30px;
`;

const Input = styled.input`
  width: 100%;
  padding: 15px;
  margin-bottom: 20px;
  background: transparent;
  border: 1px solid #555;
  border-radius: 4px;
  color: #fff;
  font-size: 16px;

  &:focus {
    border-color: #888;
    outline: none;
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 15px;
  margin-bottom: 20px;
  background: transparent;
  border: 1px solid #555;
  border-radius: 4px;
  color: #fff;
  font-size: 16px;
  height: 150px;

  &:focus {
    border-color: #888;
    outline: none;
  }
`;

const Button = styled.button`
  padding: 15px;
  width: 100%;
  background: #000;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 18px;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: #444;
  }
`;

const ContactDetails = styled.div`
  background: #333;
  padding: 40px;
  border-radius: 8px;
  text-align: center;
  width: 100%;
  max-width: 600px;
`;

const Contact = () => {
  return (
    <ContactSection>
      <FormWrapper>
        <h2>Contact Me</h2>
        <form>
          <Input type="text" placeholder="Your Name" />
          <Input type="email" placeholder="Your Email" />
          <TextArea placeholder="Your Message" />
          <Button type="submit">Send Message</Button>
        </form>
      </FormWrapper>
      <ContactDetails>
        <h3>Get in Touch</h3>
        <p>Phone: (123) 456-7890</p>
        <p>Email: contact@example.com</p>
        <div>
          {/* Social media icons */}
        </div>
        {/* Optionally include a map */}
      </ContactDetails>
    </ContactSection>
  );
};

export default Contact;
