import React, { useState, useEffect } from 'react';
import './index.css';

const Resume = () => {
  const [visibleSteps, setVisibleSteps] = useState(1);

  // Increment steps when user scrolls up
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      if (scrollY > 100) {
        setVisibleSteps(prev => prev + 1);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Data for your resume, modify as needed
  const resumeData = [
    { title: 'Objective', content: 'To secure an entry-level position in software development where I can leverage my strong academic background...' },
    { title: 'Education', content: 'B-Tech in ECE (76.5%) - Ramachandra College of Engineering, Sri Chaitanya Jr College, Zilla Parishad High School' },
    { title: 'Certificates', content: 'HTML, CSS, Java, Python, SQL, JavaScript, ReactJs' },
    { title: 'Internships', content: 'Blackbuck Engineers Pvt Ltd: Currency Exchange Rate Forecasting' },
    { title: 'Skills', content: 'Python, Java, React, HTML, CSS, MySQL, Teamwork, Leadership' },
    { title: 'Achievements', content: 'Second place in Code Royal competition, other participations' },
    { title: 'Projects', content: 'Portfolio Website and Currency Exchange Forecasting Project' },
  ];

  return (
    <div className="resume-container">
      {resumeData.slice(0, visibleSteps).map((step, index) => (
        <div className="resume-step" key={index}>
          <h2>{step.title}</h2>
          <p>{step.content}</p>
        </div>
      ))}
    </div>
  );
};

export default Resume;
